  created() {
    this.$store.dispatch("loadingBeginAct");
    service.getYuklemeYilListesi().then(yil_list => {
      this.yil_listesi = yil_list;
      this.select_yil = yil_list[0];
      this.yil_baslik = this.select_yil.yil;
