        this.ay_baslik = this.select_aystr;
        this.yukleme_listesi_yukle();
      });
    });
    service.getYuklemeRapor(this.select_yil.yil, this.select_ay.ay).then(data => {
      this.aylik_yukleme_listesi = data.aylik_yukleme_listesi;
